function setup() {
  map = map1;
  ScreenSize = 512;
  createCanvas(ScreenSize * 2, ScreenSize);
  MapDim = map.length;
  CellSize = ScreenSize / MapDim;
  MapSize = MapDim ^ 2;
  MapColor = "black";
  px = 5 * CellSize;
  py = 5 * CellSize;
  ps = ScreenSize / (512 / 16);
  pa = 1;
  pc = "black";
  rotmod = radians(3);
  Guidelength = 45;
  GuideColor = "darkred";
  BackgroundColor = "black";
  RayColor = [64, 222, 0];
  fov = 90;
  lengthv = 0;
  lengthh = 0;
  anglestep = 1;
  linewidth = ScreenSize / fov;
  colmag = 1;
  pspeedconst = 3;
  pspeed = ScreenSize / (512 / pspeedconst);
  mode = 1;
  corr = 1;
  coll = 0;
  texturingheight = 1;
  text2=loadImage('texty.png.png');
}

function keyReleased() {
  //print("g");
  if (key === "g") {
    // G - Line/Square mode
    // 0 Lines, 1 Square, 2 TEXTURED, 3 Textured pixels, 4 OFF
    if (mode == 4) {
      mode = 0;
    } else {
      mode += 1;
    }
  } else if (key === "f") {
    // F
    corr = -corr; // 1 Anti-fishbowl, -1 fishbowl
  } else if (key === 'r'||mapchangereq==1) {
    //R
    respawnreq=1;
    mapchangereq=0;
    switch (map) {
      case map1:
        pspeedconst = 1.5;
        map=map2;
        break
      case map2:
        pspeedconst = 1;
        map=map3
        break;
      case map3:
        pspeedconst = 2.5;
        map=map1;
        break;
    }
    pspeed = ScreenSize / (512 / pspeedconst);
    MapDim = map.length;
    CellSize = ScreenSize / MapDim;
    MapSize = MapDim ^ 2;
  } else if (key === 'space') {
    interacting=1;
  }
}

